function init() {
  var container = document.createElement('div'); //var avatar = document.getElementById('avatar');

  container.classList.add("columnAvatar");
  container.id = "avatardiv";
  document.body.appendChild(container);
  camera = new THREE.PerspectiveCamera(45, container.clientWidth / container.clientHeight, 1, 2000);
  camera.position.set(50, 150, 250);
  scene = new THREE.Scene();
  scene.background = new THREE.Color(0xffffff);
  scene.fog = new THREE.Fog(0xffffff, 200, 1000);
  var hemiLight = new THREE.HemisphereLight(0xffffff, 0x444444);
  hemiLight.position.set(0, 200, 0);
  scene.add(hemiLight);
  var lights = new THREE.DirectionalLight(0xffffff, 1, 0);
  lights.position.set(50, 100, 200);
  scene.add(lights);
  var dirLight = new THREE.DirectionalLight(0xffffff);
  dirLight.position.set(0, 200, 100);
  dirLight.castShadow = true;
  dirLight.shadow.camera.top = 180;
  dirLight.shadow.camera.bottom = -100;
  dirLight.shadow.camera.left = -120;
  dirLight.shadow.camera.right = 120;
  scene.add(dirLight); // scene.add( new THREE.CameraHelper( dirLight.shadow.camera ) );
  // ground

  var mesh = new THREE.Mesh(new THREE.PlaneGeometry(2000, 2000), new THREE.MeshPhongMaterial({
    color: 0x999999,
    depthWrite: false
  }));
  mesh.rotation.x = -Math.PI / 2;
  mesh.receiveShadow = true;
  scene.add(mesh);
  var grid = new THREE.GridHelper(2000, 20, 0x000000, 0x000000);
  grid.material.opacity = 0.2;
  grid.material.transparent = true; //scene.add(grid);
  // model

  var fileLoad;
  var loader = new FBXLoader();

  if (avatarState == "talking") {
    fileLoad = 'david_talking.fbx';
  } else if (avatarState == "idle") {
    fileLoad = 'david_idle.fbx';
  }

  loader.load(fileLoad, function (object) {
    mixer = new THREE.AnimationMixer(object);
    var action = mixer.clipAction(object.animations[0]);
    action.play();
    object.traverse(function (child) {
      if (child.isMesh) {
        child.castShadow = true;
        child.receiveShadow = true;
      }
    });
    scene.add(object);
  });
  renderer = new THREE.WebGLRenderer({
    antialias: true
  });
  renderer.setPixelRatio(window.devicePixelRatio);
  renderer.setSize(container.clientWidth, container.clientHeight);
  renderer.shadowMap.enabled = true;
  renderer.setClearColor(0xffffff, 0);
  container.appendChild(renderer.domElement);
  var controls = new OrbitControls(camera, renderer.domElement);
  controls.target.set(0, 100, 0);
  controls.update();
  window.addEventListener('resize', onWindowResize); // stats

  stats = new Stats();
  container.appendChild(stats.dom);
}

function onWindowResize() {
  var container = document.getElementById("avatardiv");
  camera.aspect = container.clientWidth / container.clientHeight;
  camera.updateProjectionMatrix();
  renderer.setSize(container.clientWidth, container.clientHeight);
} //
//var animationAvatar;


function animate() {
  requestAnimationFrame(animate);
  var delta = clock.getDelta();
  if (mixer) mixer.update(delta);
  renderer.render(scene, camera);
  stats.update();
} //var doneSpeaking = false;


var synth = window.speechSynthesis;
var voiceList = window.speechSynthesis.getVoices;

function greet(greetingSpeech, selectedVoice) {
  //speechSynthesis.cancel();
  greetingSpeech.voice = voiceList[selectedVoice]; //doneSpeaking = false;

  synth.speak(greetingSpeech); //greetingSpeech.onend = function () {
