  loader.load(fileLoad, function (object) {
    mixer = new THREE.AnimationMixer(object); //actions = object;
    //const action = mixer.clipAction(object.animations[0]);
    //actions = action;
    //action.play();
    //actions.push(action);
    //actions[0].play();

    var loader2 = new FBXLoader();
    fileLoad = 'david_talking.fbx';
    loader2.load(fileLoad, function (object2) {
      //let mixer2 = new THREE.AnimationMixer(object2);
      //const action2 = mixer.clipAction(object2.animations[0]);
      //actions.push(action2)
      //alert(object2.animations[0].name);
      object.animations.push(object2.animations[0]); //alert("Length of object" + object.animations.length)

      object.traverse(function (child) {
        if (child.isMesh) {
          child.castShadow = true;
          child.receiveShadow = true;
        }
      });

      if (object.animations.length == 3) {
        scene.add(object); //let object = scene.getObjectByName("avatar_animation");
        //alert("Size of object after pushing is" + object.animations.length)

        actions = [mixer.clipAction(object.animations[0]), mixer.clipAction(object.animations[2])]; //alert(actions);

        actions[0].play();
        activeAction = actions[0];
        lastAction = actions[0]; //alert(actions);
      }
    });
  });
  renderer = new THREE.WebGLRenderer({
    antialias: true
  });
  renderer.setPixelRatio(window.devicePixelRatio);
  renderer.setSize(container.clientWidth, container.clientHeight);
  renderer.shadowMap.enabled = true;
  renderer.setClearColor(0xffffff, 0);
  container.appendChild(renderer.domElement);
  var controls = new OrbitControls(camera, renderer.domElement);
  controls.target.set(0, 100, 0);
  controls.update();
  window.addEventListener('resize', onWindowResize); // stats

  stats = new Stats();
  container.appendChild(stats.dom);
}

function onWindowResize() {
  var container = document.getElementById("avatardiv");
  camera.aspect = container.clientWidth / container.clientHeight;
  camera.updateProjectionMatrix();
  renderer.setSize(container.clientWidth, container.clientHeight);
} //
